import React, { Component, useContext, useEffect, useState } from 'react';
import { View, Text, StyleSheet, Image, Dimensions, ActivityIndicator, Alert, TextInput, Pressable } from 'react-native';
import TwokLoader from './ViewModel/TwokLoader';
import ComunicationController from './Model/ComunicationController';
import * as ImagePicker from 'expo-image-picker';
import { UserContext } from './Model/UserContext';

const PersonalProfile = () => {

  const SID = useContext(UserContext)

  const [name, setName] = useState()
  const [uid, setUid] = useState()
  const [editMode, setEditMode] = useState(false)
  const [pic, setPic] = useState("")

  useEffect(() => {
    getProfilo()
  }, [])

  const getProfilo = async () => {
    let profilo = await ComunicationController.serverReq("getProfile", { sid: SID })
    setName(profilo.name)
    setUid(profilo.uid)
    if (profilo.picture != null) setPic(profilo.picture)
  }

  const editName = async () => {
    setEditMode(false)
    let r = await ComunicationController.serverReq("setProfile", { sid: SID, name: name })
    if (r != null) Alert.alert("Nome cambiato con successo!")
  }

  const pickImage = async () => {
    let result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.All,
      allowsEditing: true,
      aspect: [1, 1],
      quality: 0.5,
      base64: true
    });
    if (result.base64.length < 137000) {
      console.log("setProfile" + JSON.stringify({ sid: SID, picture: result.base64 }))
      setPic(result.base64)
      let r = await ComunicationController.serverReq("setProfile", { sid: SID, picture: result.base64 })
      if (r != null) Alert.alert("Caricata con successo!")
      return
    }
    Alert.alert("Immagine troppo grande");
  }

  return (editMode ?
    <View style={{ flex: 1 }}>
      <View style={{ flex: 2 }}>
        <TextInput style={{
          fontSize: 30,
          fontWeight: "700"
        }} onEndEditing={() => editName()} value={name} onChangeText={text => setName(text)} />
      </View>
    </View>
    :
    <View style={{ flex: 1 }}>
      <View style={{ backgroundColor: `#fffafa` }}>
        <Text style={{ paddingHorizontal: 14, paddingVertical: 4 }}>
          <View ><Image style={styles.logo} source={{ uri: 'data:image/png;base64,' + pic }} /></View>
          <View><Text style={{ fontSize: 40, fontWeight: "700" }} onLongPress={() => setEditMode(true)}>{"  " + name}</Text></View>
        </Text>
        <Pressable onPress={() => pickImage()}>
          <Text style={{ fontSize: 9, marginHorizontal: 8 }}>Cambia immagine</Text>
        </Pressable>
      </View>

      <View style={{ height: Dimensions.get('window').height / 1.4 }}>
        <TwokLoader MINE={true} uid={uid} />
      </View>
    </View>)
}

const styles = StyleSheet.create({
  logo: {
    borderRadius: 100,
    width: 60,
    height: 60,
  },
});

export default PersonalProfile;